4 #include <SFML/Graphics.hpp>
5 #include "TileSet.h"
6 #include "Layer.h"
7 #include <cmath>
8 
9 class TileMap : public sf::Drawable, public sf::Transformable {
10 public:
11     bool load(TileSet tileset, Layer layer) {
12         tex.loadFromFile(tileset.getImage());
13 
14         verts.setPrimitiveType(sf::Quads);
15         verts.resize(layer.getWidth() * layer.getHeight() * 4);
16 
17         std::vector<int> data = layer.getData();
18 
19         for (size_t i = 0; i < data.size(); i++) {
20             if (data[i] != 0) {
21                 size_t columns = tileset.getColumns();
22                 size_t val = data[i] - tileset.getFirstGid();
23 
24                 size_t y = floor(val / columns);
25                 size_t x = val % columns;
26 
27                 size_t yPos = floor(i / layer.getWidth());
28                 size_t xPos = i % layer.getWidth();
29 
30                 sf::Vertex* quad = &verts[i*4];
31 
32                 float xPos1 = xPos * tileset.getTileWidth();
33                 float xPos2 = (xPos + 1) * tileset.getTileWidth();
34                 float yPos1 = yPos * tileset.getTileHeight();
35                 float yPos2 = (yPos + 1) * tileset.getTileHeight();
36 
37                 float x1 = x * tileset.getTileWidth();
38                 float x2 = (x + 1) * tileset.getTileWidth();
39                 float y1 = y * tileset.getTileHeight();
40                 float y2 = (y + 1) * tileset.getTileHeight();
41 
42                 quad[0].position = sf::Vector2f(xPos1, yPos1);
43                 quad[1].position = sf::Vector2f(xPos2, yPos1);
44                 quad[2].position = sf::Vector2f(xPos2, yPos2);
45                 quad[3].position = sf::Vector2f(xPos1, yPos2);
46 
47                 quad[0].texCoords = sf::Vector2f(x1, y1);
48                 quad[1].texCoords = sf::Vector2f(x2, y1);
49                 quad[2].texCoords = sf::Vector2f(x2, y2);
50                 quad[3].texCoords = sf::Vector2f(x1, y2);
51             }
52         }
53         return true;
54     }
55 private:
56     virtual void draw(sf::RenderTarget& target, sf::RenderStates states) const {
57         states.transform *= getTransform();
58         states.texture = &tex;
59         target.draw(verts, states);
60     }
61 
62     sf::VertexArray verts;
63     sf::Texture tex;
64 };
65 
66 
67 #endif //PLATFORMER2D_TILEMAP_H
