1176         kBooleanSchemaType,
1177         kObjectSchemaType,
1178         kArraySchemaType,
1179         kStringSchemaType,
1180         kNumberSchemaType,
1181         kIntegerSchemaType,
1182         kTotalSchemaType
1183     };
