1500         ~PatternProperty() {
1501             if (pattern) {
1502                 pattern->~RegexType();
1503                 AllocatorType::Free(pattern);
1504             }
1505         }
1506         const SchemaType* schema;
1507         RegexType* pattern;
1508     };
