2072     void Reset() {
2073         while (!schemaStack_.Empty())
2074             PopSchema();
2075         documentStack_.Clear();
2076         ResetError();
2077     }
2078 
2080     void ResetError() {
2081         error_.SetObject();
2082         currentError_.SetNull();
2083         missingDependents_.SetNull();
2084         valid_ = true;
2085     }
