2317 #define RAPIDJSON_STRING_(name, ...) \
2318     static const StringRefType& Get##name##String() {\
2319         static const Ch s[] = { __VA_ARGS__, '\0' };\
2320         static const StringRefType v(s, static_cast<SizeType>(sizeof(s) / sizeof(Ch) - 1)); \
2321         return v;\
2322     }
